    <script>
        gsap.registerPlugin(ScrollTrigger);

        function splitTextToChars(element) {
            const text = element.innerHTML;
            let chars = '';
            let tempDiv = document.createElement('div');
            tempDiv.innerHTML = text;

            const processNode = (node) => {
                if (node.nodeType === Node.TEXT_NODE) {
                    return node.textContent.split('').map(char => {
                        if (char === '\n' || char === '\r' || char === '\t') {
                            return '';
                        }
                        if (char === ' ') {
                            return `<span class="char" style="display: inline-block; padding: 0 0.15em;"></span>`;
                        }
                        return `<span class="char" style="display: inline-block; opacity: 0;">${char}</span>`;
                    }).join('');
                } else if (node.nodeType === Node.ELEMENT_NODE) {
                    if (node.nodeName.toLowerCase() === 'br') {
                        return '<br>';
                    }
                    let inner = '';
                    node.childNodes.forEach(child => {
                        inner += processNode(child);
                    });
                    return `<${node.nodeName.toLowerCase()}>${inner}</${node.nodeName.toLowerCase()}>`;
                }
                return '';
            };

            tempDiv.childNodes.forEach(child => {
                chars += processNode(child);
            });

            chars = chars.replace(/(<br>\s*)+/g, '<br>');
            element.innerHTML = chars;

            return element.querySelectorAll('.char');
        }

        function animateChars(charElements, duration, onComplete) {
            const stagger = duration / charElements.length;

            return gsap.fromTo(charElements, {
                opacity: 0,
                y: 20
            }, {
                opacity: 1,
                y: 0,
                duration: 0.3,
                stagger: stagger,
                ease: "power2.out",
                onComplete: onComplete || null
            });
        }

        // === Presets (run immediately, before loading overlay hides) ===

        var heroUl = document.querySelector('#home-hero ul');
        var heroListItems = heroUl.querySelectorAll('li');
        gsap.set(heroListItems, {
            opacity: 0
        });

        var h1 = document.querySelector('#home-hero h1');
        var h1Chars = splitTextToChars(h1);

        var h2Data = [];
        document.querySelectorAll('main section h2').forEach(function(h2) {
            var section = h2.closest('section');
            var chars = splitTextToChars(h2);
            h2Data.push({
                section: section,
                chars: chars
            });
        });

        var frameTop = document.querySelector('#home-frame .picture');
        gsap.set(frameTop, {
            opacity: 0,
            y: -50
        });

        var galleryItems = document.querySelectorAll('#home-gallery .row ul.col li');
        galleryItems.forEach(function(item) {
            gsap.set(item, {
                opacity: 0,
                y: 40
            });
        });

        var footerSvgPath = document.querySelector('footer svg path');
        if (footerSvgPath) {
            var footerPathLength = footerSvgPath.getTotalLength();
            footerSvgPath.style.stroke = '#DBC2A3';
            footerSvgPath.style.strokeWidth = '1.5';
            footerSvgPath.style.fill = 'transparent';
            footerSvgPath.style.strokeDasharray = footerPathLength;
            footerSvgPath.style.strokeDashoffset = footerPathLength;
        }

        // Menu overlay setup (event listeners + SVG preset)
        var menuBtn = document.querySelector('header .menu');
        var menuOverlay = document.querySelector('header .menu-overlay');
        var closeMenuBtn = document.querySelector('header .close-menu');
        var menuLinks = document.querySelectorAll('header .menu-overlay .main ul li a');

        var menuSvgPath = document.querySelector('header .menu-overlay .footer svg path');
        var menuSvgTl = null;

        if (menuSvgPath) {
            var menuPathLength = menuSvgPath.getTotalLength();
            menuSvgPath.style.stroke = '#DBC2A3';
            menuSvgPath.style.strokeWidth = '1.5';
            menuSvgPath.style.fill = 'transparent';
            menuSvgPath.style.strokeDasharray = menuPathLength;
            menuSvgPath.style.strokeDashoffset = menuPathLength;

            menuSvgTl = gsap.timeline({
                paused: true
            });
            menuSvgTl.to(menuSvgPath, {
                strokeDashoffset: 0,
                fill: '#DBC2A3',
                strokeWidth: 0,
                duration: 3,
                ease: "power1.inOut"
            });
        }

        var menuItems = document.querySelectorAll('header .menu-overlay .main ul li');

        menuBtn.addEventListener('click', function() {
            menuOverlay.classList.add('active');
            document.body.style.overflow = 'hidden';
            if (menuSvgTl) {
                menuSvgTl.timeScale(1);
                menuSvgTl.restart();
            }

            gsap.set(menuItems, {
                opacity: 0,
                y: 30
            });
            gsap.to(menuItems, {
                opacity: 1,
                y: 0,
                duration: 0.5,
                stagger: 0.12,
                ease: "power3.out",
                delay: 0.15
            });
        });

        closeMenuBtn.addEventListener('click', function() {
            gsap.to(menuItems, {
                opacity: 0,
                y: -15,
                duration: 0.25,
                stagger: 0.05,
                ease: "power2.in",
                onComplete: function() {
                    menuOverlay.classList.remove('active');
                    document.body.style.overflow = '';
                }
            });
            if (menuSvgTl) {
                menuSvgTl.timeScale(4);
                menuSvgTl.reverse();
            }
        });

        menuLinks.forEach(function(link) {
            link.addEventListener('click', function() {
                gsap.to(menuItems, {
                    opacity: 0,
                    y: -15,
                    duration: 0.25,
                    stagger: 0.05,
                    ease: "power2.in",
                    onComplete: function() {
                        menuOverlay.classList.remove('active');
                        document.body.style.overflow = '';
                    }
                });
                if (menuSvgTl) {
                    menuSvgTl.timeScale(4);
                    menuSvgTl.reverse();
                }
            });
        });

        // Services accordion (no animation, just click handler)
        document.querySelectorAll('#home-services ul.services li').forEach(function(item) {
            item.addEventListener('click', function() {
                if (this.classList.contains('expanded')) return;

                var expandedItem = document.querySelector('#home-services ul.services li.expanded');
                var isBase = this.classList.contains('base');
                var self = this;
                var body = self.querySelector('.body');

                if (expandedItem) {
                    var oldBody = expandedItem.querySelector('.body');
                    // Collapse old item
                    gsap.to(oldBody, {
                        height: 0,
                        opacity: 0,
                        duration: 0.4,
                        ease: "power2.inOut",
                        onComplete: function() {
                            expandedItem.classList.remove('expanded');
                            oldBody.style.height = '';
                            oldBody.style.opacity = '';
                            expandNewItem();
                        }
                    });
                } else {
                    expandNewItem();
                }

                function expandNewItem() {
                    self.classList.add('expanded');

                    if (isBase) {
                        // BASE animation: elastic flip-in with staggered reveal
                        var cards = body.querySelectorAll(':scope > ul > li');
                        gsap.set(body, {
                            height: 'auto',
                            opacity: 1
                        });
                        var fullHeight = body.offsetHeight;
                        gsap.set(body, {
                            height: 0,
                            opacity: 0
                        });

                        var tl = gsap.timeline();

                        // Expand the body container
                        tl.to(body, {
                            height: fullHeight,
                            opacity: 1,
                            duration: 0.6,
                            ease: "power2.out",
                            onComplete: function() {
                                body.style.height = 'auto';
                            }
                        });

                        if (cards.length) {
                            gsap.set(cards, {
                                opacity: 0,
                                y: 30
                            });

                            tl.to(cards, {
                                opacity: 1,
                                y: 0,
                                duration: 0.7,
                                stagger: 0.15,
                                ease: "power2.out"
                            }, "-=0.3");

                            cards.forEach(function(card, cardIndex) {
                                var innerItems = card.querySelectorAll('ul.main li');
                                if (innerItems.length) {
                                    gsap.fromTo(innerItems, {
                                        opacity: 0,
                                        y: 12
                                    }, {
                                        opacity: 1,
                                        y: 0,
                                        duration: 0.4,
                                        stagger: 0.05,
                                        ease: "power2.out",
                                        delay: 0.4 + cardIndex * 0.2
                                    });
                                }
                                var footer = card.querySelector('.footer');
                                if (footer) {
                                    gsap.fromTo(footer, {
                                        opacity: 0,
                                        y: 10
                                    }, {
                                        opacity: 1,
                                        y: 0,
                                        duration: 0.5,
                                        ease: "power2.out",
                                        delay: 0.7 + cardIndex * 0.2
                                    });
                                }
                            });
                        }
                    } else {
                        // NON-BASE animation: curtain reveal + content fade cascade
                        gsap.set(body, {
                            height: 'auto',
                            opacity: 1
                        });
                        var fullHeight = body.offsetHeight;
                        gsap.set(body, {
                            height: 0,
                            opacity: 1,
                            overflow: 'hidden'
                        });

                        var contentElements = body.querySelectorAll(':scope > p, :scope > ul > li, :scope > span.link');
                        gsap.set(contentElements, {
                            opacity: 0,
                            y: 30,
                            filter: 'blur(4px)'
                        });

                        var tl = gsap.timeline();

                        tl.to(body, {
                            height: fullHeight,
                            duration: 0.6,
                            ease: "power4.out",
                            onComplete: function() {
                                body.style.height = 'auto';
                                body.style.overflow = '';
                            }
                        });

                        tl.to(contentElements, {
                            opacity: 1,
                            y: 0,
                            filter: 'blur(0px)',
                            duration: 0.4,
                            stagger: 0.08,
                            ease: "power2.out"
                        }, "-=0.25");
                    }
                }
            });
        });

        // Services popup
        var popupOverlay = document.getElementById('services-popup-overlay');
        var popupTitle = document.getElementById('services-popup-title');
        var popupList = document.getElementById('services-popup-list');
        var popupClose = document.querySelector('.services-popup-close');
        var popup = document.querySelector('.services-popup');

        var weddingExtras = [
            'Сватбена фотосесия в различен ден — <strong>150€</strong> <small>(транспортът на повече от 100 км от Велико Търново се поема от клиента)</small>',
            'Овъртайм — <strong>80€/час</strong>',
            'Експресно изпращане на снимките (за 10 дни) — <strong>200€</strong>',
            'Изготвяне на индивидуален пакет'
        ];

        var otherExtras = [
            'Овъртайм — <strong>80€/час</strong>',
            'Експресно изпращане на снимките (за 10 дни) — <strong>200€</strong>',
            'Луксозен Фотоалбум с 30 избрани кадри във формат 15х20 на премиум хартия — <strong>70€</strong> <small>(кръщене)</small>',
            'Изготвяне на индивидуален пакет'
        ];

        function openServicesPopup(isWedding) {
            var items = isWedding ? weddingExtras : otherExtras;
            popupTitle.textContent = 'Допълнителни услуги';
            popupList.innerHTML = items.map(function(item) {
                return '<li>' + item + '</li>';
            }).join('');

            popupOverlay.classList.add('active');
            document.body.style.overflow = 'hidden';

            var listItems = popupList.querySelectorAll('li');
            gsap.set(popup, { opacity: 0, y: 40 });
            gsap.set(listItems, { opacity: 0, y: 20 });

            gsap.to(popup, {
                opacity: 1,
                y: 0,
                duration: 0.5,
                ease: "power2.out"
            });

            gsap.to(listItems, {
                opacity: 1,
                y: 0,
                duration: 0.4,
                stagger: 0.08,
                ease: "power2.out",
                delay: 0.25
            });
        }

        function closeServicesPopup() {
            gsap.to(popup, {
                opacity: 0,
                y: 30,
                duration: 0.3,
                ease: "power2.in",
                onComplete: function() {
                    popupOverlay.classList.remove('active');
                    document.body.style.overflow = '';
                }
            });
        }

        popupClose.addEventListener('click', function(e) {
            e.stopPropagation();
            closeServicesPopup();
        });

        popupOverlay.addEventListener('click', function(e) {
            if (e.target === popupOverlay) {
                closeServicesPopup();
            }
        });

        document.querySelectorAll('#home-services ul.services > li span.link').forEach(function(link) {
            link.addEventListener('click', function(e) {
                e.stopPropagation();
                var serviceItem = this.closest('#home-services ul.services > li');
                var isWedding = serviceItem.querySelector('b') &&
                    serviceItem.querySelector('b').textContent.trim() === 'Сватбен ден';
                openServicesPopup(isWedding);
            });
        });

        // === Animation triggers (run after loading overlay hides) ===

        function initAnimations() {
            // H1 char animation
            animateChars(h1Chars, 1, initListAnimation);

            // H2 scroll-triggered char animations
            h2Data.forEach(function(data) {
                ScrollTrigger.create({
                    trigger: data.section,
                    start: 'top 60%',
                    onEnter: function() {
                        animateChars(data.chars, 1);
                    },
                    onLeaveBack: function() {
                        gsap.set(data.chars, {
                            opacity: 0,
                            y: 20
                        });
                    }
                });
            });

            // Parallax for #home-frame .picture
            gsap.to(frameTop, {
                y: 100,
                ease: "none",
                scrollTrigger: {
                    trigger: '#home-frame',
                    start: 'top 90%',
                    end: 'bottom 20%',
                    scrub: 1.5,
                }
            });

            gsap.to(frameTop, {
                opacity: 1,
                ease: "power1.inOut",
                scrollTrigger: {
                    trigger: '#home-frame',
                    start: 'top 90%',
                    end: 'top 20%',
                    scrub: 1.5,
                    onLeaveBack: function() {
                        gsap.to(frameTop, {
                            opacity: 0,
                            duration: 0.8,
                            ease: "power1.inOut"
                        });
                    }
                }
            });

            // Gallery items fade-in
            galleryItems.forEach(function(item) {
                gsap.to(item, {
                    opacity: 1,
                    y: 0,
                    duration: 1,
                    ease: "power2.out",
                    scrollTrigger: {
                        trigger: item,
                        start: 'top 70%',
                        toggleActions: 'play none none reverse'
                    }
                });
            });

            // Footer SVG drawing animation
            if (footerSvgPath) {
                var svgTl = gsap.timeline({
                    scrollTrigger: {
                        trigger: 'footer svg',
                        start: 'top 80%',
                        toggleActions: 'play none none reverse'
                    }
                });

                svgTl.to(footerSvgPath, {
                    strokeDashoffset: 0,
                    fill: '#DBC2A3',
                    strokeWidth: 0,
                    duration: 3,
                    ease: "power1.inOut"
                });

                ScrollTrigger.create({
                    trigger: 'footer svg',
                    start: 'top 80%',
                    onLeaveBack: function() {
                        svgTl.timeScale(4);
                        svgTl.reverse();
                    },
                    onEnter: function() {
                        svgTl.timeScale(1);
                        svgTl.play();
                    }
                });
            }
        }

        function initListAnimation() {
            gsap.set(heroListItems, {
                opacity: 0
            });
            gsap.set([heroListItems[0], heroListItems[1]], {
                x: 200
            });
            gsap.set([heroListItems[3], heroListItems[4]], {
                x: -200
            });

            var tl = gsap.timeline({
                scrollTrigger: {
                    trigger: '#home-hero ul',
                    start: 'top 50%',
                    toggleActions: 'play none none reverse',
                    onLeaveBack: function() {
                        gsap.to(heroListItems, {
                            opacity: 0,
                            duration: 0.3,
                            ease: "power2.out",
                            onComplete: function() {
                                tl.progress(0);
                            }
                        });
                    }
                }
            });

            tl.to(heroListItems[2], {
                    opacity: 1,
                    duration: 0.6,
                    ease: "power2.out"
                })
                .to([heroListItems[0], heroListItems[1]], {
                    opacity: 1,
                    x: 0,
                    duration: 0.8,
                    stagger: 0.15,
                    ease: "power2.out"
                }, "-=0.2")
                .to([heroListItems[3], heroListItems[4]], {
                    opacity: 1,
                    x: 0,
                    duration: 0.8,
                    stagger: 0.15,
                    ease: "power2.out"
                }, "<");
        }

        //

        // Loading overlay
        const loadingOverlay = document.getElementById('loading-overlay');
        const loadStart = Date.now();

        window.addEventListener('load', function() {
            const elapsed = Date.now() - loadStart;
            const remaining = Math.max(0, 2000 - elapsed);

            setTimeout(function() {
                gsap.to(loadingOverlay, {
                    // y: '-100%',
                    opacity: 0,
                    duration: 0.8,
                    ease: 'power3.inOut',
                    onComplete: function() {
                        loadingOverlay.remove();
                        initAnimations();
                    }
                });
            }, remaining);
        });
    </script>

</body>

</html>
